import React, { Component } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Axios from 'axios';

class ResourceManager extends Component {
  constructor(props) {
    super(props);
    this.state = {
      columns: [{ "header": "ID", "name": "resourceManagerId" },
      { "header": "NAME", "name": "name" },
      { "header": "Start Date", "name": "startDate" },
      { "header": "End Date", "name": "endDate" }],
      showAdd: false,
      showUpdate: false,
      showDelete: false,
      showRM: true,
      id: null,
      name: null,
      startDate: null,
      startDate2: null,
      endDate: null,
      endDate2: null
    }
    this.handleAdd = this.handleAdd.bind(this);
    this.handleUpdate = this.handleUpdate.bind(this);
    this.handleDelete = this.handleDelete.bind(this);
    this.handleClose = this.handleClose.bind(this);
    this.handleSubmitAdd = this.handleSubmitAdd.bind(this);
    this.handleSubmitUpdate = this.handleSubmitUpdate.bind(this);
  }

  handleSubmitAdd(event) {
    event.preventDefault();
    console.log("Form Add submitted");
    if (this.state.name) {
      let userData = {
        name: this.state.name,
        startDate: this.state.startDate,
        endDate: this.state.endDate
      }
      debugger
      Axios({
        url: 'http://localhost:9091/resource_manager/',
        method: "post",
        data: userData,
        headers: {
          'Content-Type': 'application/json'
        }

      }).then(res => {
        debugger
        console.log(this.props);
      }).catch(err => {
        console.log(err);
      })
    }

    this.setState({
      id: null,
      name: null,
      startDate: null,
      endDate: null,
      showAdd: false,
      showDelete: false,
      showUpdate: false,
      showRM: false
    })
  }

  handleSubmitUpdate(event) {
    event.preventDefault();
    console.log("Form Update submitted");
    if (this.state.name) {
      var actualStartDate = this.state.startDate2;
      if (actualStartDate == null){
        actualStartDate = this.state.startDate;
      }
      else{
        debugger
        actualStartDate = this.state.startDate2;
        console.log(actualStartDate);
      }
      console.log(actualStartDate);
      let userData = {
        resourceManagerId: this.state.id,
        name: this.state.name,
        startDate: this.state.actualStartDate,
        endDate: this.state.endDate2
      }
      debugger
      Axios({
        url: 'http://localhost:9091/resource_manager/' + this.state.id,
        method: "put",
        data: userData,
        headers: {
          'Content-Type': 'application/json'
        }

      }).then(res => {
        debugger
        console.log(this.props);
      }).catch(err => {
        console.log(err);
      })
    }
    this.setState({
      id: null,
      name: null,
      startDate: null,
      endDate: null,
      showAdd: false,
      showDelete: false,
      showUpdate: false,
      showRM: false
    })
  }

  handleSubmitDelete(event) {
    // event.preventDefault();
    console.log("Form Delete submitted");
    debugger
    if (this.state.id) {
      Axios({
        url: 'http://localhost:9091/resource_manager/' + this.state.id,
        method: "delete",
        data: "",
        headers: {
          'Content-Type': 'application/json'
        }

      }).then(res => {
        event.preventDefault();
        debugger
        console.log(this.props);
      }).catch(err => {
        console.log(err);
      })
    }
    this.setState({
      id: null,
      name: null,
      startDate: null,
      endDate: null,
      showAdd: false,
      showDelete: false,
      showUpdate: false,
      showRM: false
    })
  }

  handleAdd(event) {
    this.setState({
      showAdd: true,
      showDelete: false,
      showUpdate: false,
      showRM: false
    })
  }

  handleUpdate = (id) => {
    debugger;
    var startD = this.props.rows[id - 1].startDate;
    var endD = this.props.rows[id - 1].endDate;

    this.setState({
      id: id,
      name: this.props.rows[id - 1].name,
      startDate: startD,
      endDate: endD,
      startDate2: null,
      endDate2: null,
      showUpdate: true,
      showAdd: false,
      showDelete: false,
      showRM: false
    })
  }

  handleDelete(event) {
    this.setState({
      showDelete: true,
      showAdd: false,
      showUpdate: false,
      showRM: false
    })
  }

  handleClose(event) {
    this.setState({
      showUpdate: false,
      showDelete: false,
      showAdd: false,
      showRM: false

    })
  }

  handleOnChange = (event) => {
    debugger
    console.log("line 88", event.target.value);
    let obj = {}

    obj[event.target.id] = event.target.value;

    this.setState({

        ...this.state.id,

        ...this.state.name,

        ...this.state.startDate,

        ...this.state.endDate,

        ...obj


    })

    console.log("onchange =? obj => ", obj)
    console.log('this.state => ', this.state)

  }



  render() {
    console.log("props--------", this.props);
    console.log("line 314", this.state);
    return (
      <div>
        <table>
          <thead>
            <tr>
              {this.state.columns.map(col =>
                <th key={col.name}>{col.header}</th>
              )}
            </tr>
          </thead>
          <tbody>
            {this.props.rows.map(row =>
              <tr key={row.id} >
                {this.state.columns.map(col =>
                  <td key={col.name}>{row[col.name]}</td>
                )}
                <Button variant="primary" onClick={() => { this.handleUpdate(row.resourceManagerId) }} > update {row.resourceManagerId} </Button>
              </tr>
            )}
          </tbody>
        </table>
        <Button variant="inherit" onClick={this.handleAdd}>
          Add
        </Button>

        <Modal show={this.state.showUpdate} onHide={this.handleClose}>
          <Modal.Header>
            <Modal.Title>Update Resource Manager</Modal.Title>
          </Modal.Header>
          <form onSubmit={this.handleSubmitUpdate}>
            <Modal.Body>
              <br />
              <label For="id">ID</label>
              <br/>
              <label For="id" >{this.state.id}</label>
              <br/>
              <label For="name">Name</label>
              <input id="name" name="name" type="text" onChange={this.handleOnChange} value={this.state.name} />
              <br/>
              <label For="startDate">Default Start Date</label>
              <br/>
              <label For="startDate" >{this.state.startDate}</label>
              <br />
              <label for="startDate">Updated Start Date</label>
              <input type="date" id="startDate" class="form-control" onChange={this.onChange} value= {this.state.startDate2}/>
              <br/>
              <label For="endDate">DefaultEnd Date</label>
