import React, { Component } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
//import Accordion from 'react-bootstrap/Accordion';
import Axios from 'axios';
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';
import $ from 'popper.js';


class WorkerToManager extends Component {
  constructor(props) {
    super(props);
    var names = [];
    var nameMap = {};
    {this.props.rmdata.map((row, index)  =>
      names.push(this.props.rmdata[index].name)
    )}
    {this.props.rmdata.map((row, index)  =>
      nameMap [this.props.rmdata[index].name]= this.props.rmata[index].resourceManagerId
    )}
    this.state = {
      columns: [ { "header": "Client", "name": "client" },
      { "header": "Resource Manager", "name": "resourceManager" }],
      showWorkerToManager: true,
      showAdd: false,
      showUpdate: false,
      showDelete: false,
      value: 'coconut',
      showWorker: false,
      showRM: false,
      resourceManagerId: null,
      workerId: this.props.id,
      client: null,
      rmdata: this.props.rmdata,
      dropdownOpen: false,
      names: names,
      nameMap: nameMap
    }
    this.handleAdd = this.handleAdd.bind(this);
    this.handleUpdate = this.handleUpdate.bind(this);
    this.handleDelete = this.handleDelete.bind(this);
    this.handleClose = this.handleClose.bind(this);
    this.handleSubmitAdd = this.handleSubmitAdd.bind(this);
    this.handleSubmitUpdate = this.handleSubmitUpdate.bind(this);
    this.handleSubmitDelete = this.handleSubmitDelete.bind(this);
    this.toggle = this.toggle.bind(this);
  }

  handleSubmitAdd(event) {
    debugger
    event.preventDefault();
    console.log("Form Add submitted");
    debugger
      let userData = {
        resourceManagerId: this.state.resourceManagerId,
        client: this.state.client,
        workerId: this.state.workerId
      }
      debugger
      Axios({
        url: 'http://localhost:9091/worker_to_manager/', 
        method: "post",
        data: userData,
        headers: {
          'Content-Type': 'application/json'
        }
      }).then(res => {//help on understanding these few lines
        debugger
        console.log(this.props);
      }).catch(err => {
        console.log(err);
      })

    this.setState({//hides the modal
      id: null,
      showAdd: false,
      showDelete: false,
      showUpdate: false,
      showWorker: false
    })
  }

  handleSubmitUpdate(event) {
    event.preventDefault();
    console.log("Form Update submitted");
    //if (this.state.resourceManagerId) {
      let userData = {
        uniqueId: this.state.id,
        workerId: this.state.workerId,
        resourceManagerId: this.state.resourceManagerId,
        client: this.state.client
      }
      debugger
      Axios({ //when updating information
        url: 'http://localhost:9091/worker_to_manager/' + this.state.id, //this.state.id= value /{id}
        method: "put",
        data: userData,
        headers: {
          'Content-Type': 'application/json'
        }

      }).then(res => {
        debugger
        console.log(this.props);
      }).catch(err => {
        console.log(err);
      })
    //}
    this.setState({
      id: null,
      resourceManagerId: null,
      workerId: null,
      showAdd: false,
      showDelete: false,
      showUpdate: false,
      showWorkerToManager: true,
      showWorker: false  
    })
  }

  handleSubmitDelete(event) {//deleteing data
    // event.preventDefault();
    console.log("Form Delete submitted");
    debugger
    if (this.state.id) {
      Axios({
        url: 'http://localhost:9091/worker_to_manager/' + this.state.id,
        method: "delete",
        data: "",
        headers: {
          'Content-Type': 'application/json'
        }

      }).then(res => {
        event.preventDefault();
        debugger
        console.log(this.props);
      }).catch(err => {
        console.log(err);
      })
    }
    this.setState({
      id: null,
      resourceManagerId: null,
      workerId: null,
      showAdd: false,
      showDelete: false,
      showUpdate: false,
      showWorkerToManager: true,
      showWorker: false
    })
  }

  handleAdd(event) {//help with this line of code
    console.log(this.state.rmdata)
    this.setState({
      showAdd: true,
      showDelete: false,
      showUpdate: false,
      showWorkerToManager: false,
      showWorker: false
    })
  }

  handleUpdate = (id) => {
    debugger;
    this.setState({
      id: this.props.rows[id].uniqueId,
      resourceManagerId: this.props.rows[id].resourceManagerId,
      workerId: this.props.rows[id].workerId,
      client: this.props.rows[id].client,
      showUpdate: true,
      showAdd: false,
      showDelete: false,
      showWorkerToManager: false
    })
  }

  handleDelete(id) {
    this.setState({
      id: this.props.rows[id].uniqueId,
      showDelete: true,
      showAdd: false,
      showUpdate: false,
      showWorkerToManager: false
    })
  }

  handleClose(event) {
    this.setState({
      showUpdate: false,
      showDelete: false,
      showAdd: false,
      showWorker: false,
      showWorkerToManager: true     
    })
  }

  handleOnChange = (event) => {
    debugger
    let obj = {}

    obj[event.target.id] = event.target.value;

    this.setState({

        ...this.state.id,

        ...this.state.resourceManagerId,

        ...this.state.client,

        ...obj


    })

    console.log("onchange =? obj => ", obj)
    console.log('this.state => ', this.state)

  }

  handleSelect(event) {
    this.setState({
      resourceManagerId: this.state.nameMap[event.value]
    })
  }

  toggle() {
    this.setState(prevState => ({
      dropdownOpen: !prevState.dropdownOpen
    }));
  }

  render() {
    console.log("props--------", this.props);
    console.log("line 314", this.state);
    return (
      <div>
        <table>
          <thead>
            <tr>
              {this.state.columns.map(col =>
                <th key={col.name}>{col.header}</th>
              )}
            </tr>
          </thead>
              <tbody>
                {this.props.rows.map((row, index)  =>
                  <tr>
                    {this.state.columns.map(col =>
                      <td key={col.name}>{row[col.name]}</td>
                    )}
                    <Button variant="primary" onClick={() => { this.handleUpdate(index) }} > update </Button>
                    <Button variant="primary" onClick={() => { this.handleDelete(index) }} > delete </Button>
                  </tr>
                )}
              </tbody>
        </table>
        <Button variant="inherit" onClick={this.handleAdd}>
          Add
        </Button>

        <Modal show={this.state.showUpdate} onHide={this.handleClose}>
          <Modal.Header>
            <Modal.Title>Update WorkerToManager</Modal.Title>
          </Modal.Header>
          <form onSubmit={this.handleSubmitUpdate}>
            <Modal.Body>
              <br />
              <label For="id">ID</label>
              <br/>
              <label For="id" >{this.state.id}</label>
              <br/>
              <label For="resourceManagerId">Resource Manager</label>
              <input id="resourceManagerId" name="resourceManagerId" type="text" onChange={this.handleOnChange} value={this.state.resourceManagerId} />
              <label For="client">Client</label>
              <input id="client" name="client" type="text" onChange={this.handleOnChange} value={this.state.client} />
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={this.handleClose}>
                Close
                </Button>
              <Button type="submit" variant="primary" >
                Submit
                </Button>
            </Modal.Footer>
          </form>
        </Modal>


        <Modal show={this.state.showDelete} onHide={this.handleClose}>
          <Modal.Header>
            <Modal.Title>Delete Worker To Manager Entry</Modal.Title>
          </Modal.Header>
          <form onSubmit={this.handleSubmitDelete}>
            <Modal.Body>
              <label For="ID">ID</label>
              <input id="ID" type="text" onChange={this.handleOnChange} value={this.state.id} />
            </Modal.Body>
