import React, { Component } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Axios from 'axios';
import BillRate from './BillRate';
import CommissionRate from './CommissionRate';
import ResourceHours from './ResourceHours';
import PayRate from './PayRate';

class Configure extends Component {
  constructor(props) {
    super(props);
    this.state = {
      columns: [{ "header": "Client", "name": "client" },
      { "header": "Worker", "name": "worker" },
      { "header": "Resource Manager", "name": "resourceManager" }],
      startDate: null,
      endDate: null
    }
    // this.handleOnChange = this.handleOnChange.bind(this);
    // this.handleAdd = this.handleAdd.bind(this);
    // this.handleUpdate = this.handleUpdate.bind(this);
    // this.handleDelete = this.handleDelete.bind(this);
    this.handleBillRate = this.handleBillRate.bind(this);
    this.handleClose = this.handleClose.bind(this);
    // this.handleSubmitAdd = this.handleSubmitAdd.bind(this);
    // this.handleSubmitUpdate = this.handleSubmitUpdate.bind(this);
    // this.handleSubmitDelete = this.handleSubmitDelete.bind(this);
  }

  handleClose(event) {
    this.setState({
      showBR: false,
      showCR: false,
      showRH: false,
      showPR: false
    })
  }

  handleBillRate = (id) => {
      debugger
      Axios({ //when updating information
        url: 'http://localhost:9091/bill_rate/getByUI/' + id,
        method: "get",
        headers: {
          'Content-Type': 'application/json'
        }

      }).then(res => {
        debugger
        console.log(this.props);
        this.setState ({
            brdata : res.data,
            id: id,
            showBR : true,
            showPR: false,
            showRH: false,
            showCR: false
        })
      }).catch(err => {
        console.log(err);
      })
  }

  handlePayRate = (id) => {
    debugger
    Axios({ //when updating information
      url: 'http://localhost:9091/pay_rate/getByUI/' + id,
      method: "get",
      headers: {
        'Content-Type': 'application/json'
      }
    }).then(res => {
      debugger
      console.log(this.props);
      this.setState ({
          prdata : res.data,
          id: id,
          showBR : false,
          showPR: true,
          showRH: false,
          showCR: false
      })
    }).catch(err => {
      console.log(err);
    })
}

  handleCommissionRate = (id) => {
    debugger
    Axios({ //when updating information
      url: 'http://localhost:9091/commission_rate/getByUI/' + id,
      method: "get",
      headers: {
        'Content-Type': 'application/json'
      }
    }).then(res => {
      debugger
      console.log(this.props);
      this.setState ({
          crdata : res.data,
          id: id,
          showBR : false,
          showPR: false,
          showRH: false,
          showCR: true
      })
    }).catch(err => {
      console.log(err);
    })
  }

  handleResourceHours = (id) => {
    debugger
    Axios({ //when updating information
      url: 'http://localhost:9091/resource_hours/getByUI/' + id,
      method: "get",
      headers: {
        'Content-Type': 'application/json'
      }
    }).then(res => {
      debugger
      console.log(this.props);
      this.setState ({
          rhdata : res.data,
          id: id,
          showBR : false,
          showPR: false,
          showRH: false,
          showCR: true
      })
    }).catch(err => {
      console.log(err);
    })
  }

  render() {
    console.log("props--------", this.props);
    console.log("line 314", this.state);
    return (
      <div>
        <table>
          <thead>
            <tr>
              {this.state.columns.map(col =>
                <th key={col.name}>{col.header}</th>
              )}
            </tr>
          </thead>
          <tbody>
            {this.props.rows.map(row =>
              <tr key={row.id} >
                {this.state.columns.map(col =>
                  <td key={col.name}>{row[col.name]}</td>
                )}

                <Button variant="primary" onClick={() => { this.handleBillRate(row.uniqueId) }} >Billing</Button>
                <Button variant="primary" onClick={() => { this.handlePayRate(row.uniqueId) }} >Pay</Button> 
                <Button variant="primary" onClick={() => { this.handleResourceHours(row.uniqueId) }} >Timesheet</Button>
                <Button variant="primary" onClick={() => { this.handleCommissionRates(row.uniqueId) }} >Commission</Button>  
              </tr>
            )}
          </tbody>
        </table>

        <Modal show={this.state.showBR} onHide={this.handleClose}>
          <Modal.Header>
            <Modal.Title>Given Bill Rates</Modal.Title>
          </Modal.Header>
            <Modal.Body>
                <BillRate rows={this.state.brdata} id={this.state.id}/>        
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={this.handleClose}>
                Close
                </Button>
            </Modal.Footer>
        </Modal>

        <Modal show={this.state.showCR} onHide={this.handleClose}>
          <Modal.Header>
            <Modal.Title>Given Commission Rates</Modal.Title>
          </Modal.Header>
            <Modal.Body>
                <CommissionRate rows={this.state.crdata} id={this.state.id}/>        
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={this.handleClose}>
                Close
                </Button>
            </Modal.Footer>
        </Modal>

        <Modal show={this.state.showRH} onHide={this.handleClose}>
          <Modal.Header>
            <Modal.Title>Given Resource Hours</Modal.Title>
          </Modal.Header>
            <Modal.Body>
                <ResourceHours rows={this.state.rhdata} id={this.state.id}/>        
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={this.handleClose}>
                Close
                </Button>
            </Modal.Footer>
        </Modal>

        <Modal show={this.state.showPR} onHide={this.handleClose}>
          <Modal.Header>
            <Modal.Title>Given Pay Rates</Modal.Title>
          </Modal.Header>
            <Modal.Body>
                <PayRate rows={this.state.prdata} id={this.state.id}/>        
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={this.handleClose}>
                Close
                </Button>
            </Modal.Footer>
        </Modal>

      </div>
    );
  }
}

export default Configure;
