import React, { Component } from 'react';
import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'
import Container from 'react-bootstrap/Container'
import Modal from 'react-bootstrap/Modal'
//import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Axios from 'axios';
import ResourceManager from './ResourceManager'
import Worker from './Worker'
import Configure from './Configure'
import { bigIntLiteral } from '@babel/types';
import CalculateCommission from './CalculateCommission';
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';
//resource manager, resource, resource customer rate, resource salary, resource hour. commission rate
class Tiles extends Component {
  constructor(props, context) {
    super(props);
    this.state = {
      showCalculateCommission: false,
      showRM: false,
      showWorker:false,
      showWorkerToManager: false,
      showConfigure:false,
      data : [],
      resourceManagerId : null,
      startDate: null,
      endDate: null,
      commissionValue: null
    }
    this.handleResourceManager = this.handleResourceManager.bind(this);
    this.handleClose = this.handleClose.bind(this);
    this.handleWorker= this.handleWorker.bind(this);
    this.handleConfigure = this.handleConfigure.bind(this);
    this.handleCalculateCommission=this.handleCalculateCommission.bind(this);
  }

  handleClose(event) {
       this.setState({
         showRM: false,
         showWorker:false,
         showConfigure:false,
         showCalculateCommission: false
       })
  }

  handleResourceManager(event) {
    event.preventDefault();
    // debugger
      Axios({
        url: 'http://localhost:9091/resource_manager/all', 
        method: "get",
        headers: {
          'Content-Type': 'application/json'
        }

      }).then(res => {
        // debugger
        console.log(res.data);
        this.setState({
          showWorker : false,
          showConfigure : false,
          showRM : true,
          //showWorker: false,
          showCalculateCommission:false,
          data : res.data
        })
      }).catch(err => {
        console.log(err);
      })
    }

    handleCalculateCommission(event){
      event.preventDefault();
      debugger
      Axios({
        url: 'http://localhost:9091/resource_manager/all',
        method: "get",
        headers: {
          'Content-Type': 'application/json'
        }
      }).then(res => {
        // debugger
        console.log(res.data);
        this.setState({
          showWorker : false,
          showConfigure : false,
          showRM : false,
          showCalculateCommission: true,
          data : res.data
        })
      }).catch(err => {
        console.log(err);
      })
    }

    handleCalculate(event) {
      event.preventDefault();
      // debugger
        Axios({
          url: 'http://localhost:9091/resource_manager/calculate_commission/' + this.state.resourceManagerId + "?startDate = " + this.state.startDate+ "&endDate=" + this.status.endDate, 
          method: "get",
          headers: {
            'Content-Type': 'application/json'
          }
        }).then(res => {
          // debugger
          console.log(res.data);
          this.setState({
            showWorker : false,
            showConfigure : false,
            showRM : false,
            showCalculateCommission: false,
            data : res.data
          })
        }).catch(err => {
          console.log(err);
        })
      }

    handleWorker(event) {
      event.preventDefault();
      // debugger
        Axios({
          url: 'http://localhost:9091/worker/all', 
          method: "get",
          headers: {
            'Content-Type': 'application/json'
          }

        }).then(res => {
          // debugger
          console.log(res.data);
          this.setState({
            showConfigure : false,
            showRM : false,
            showWorker : true,
            //showRM: false,
            showCalculateCommission:false,
            data : res.data
          })
        }).catch(err => {
          console.log(err);
        })
      }

      handleOnChange = (event) => {
          debugger
          console.log("line 88", event.target.value);
          let obj = {}

          obj[event.target.id] = event.target.value;

          this.setState({

            ...this.state.startDate,

            ...this.state.endDate,

            ...obj


          })
        }

      handleConfigure(event) {
        event.preventDefault();
        //debugger
          Axios({
            url: 'http://localhost:9091/worker_to_manager/all', 
            method: "get",
            headers: {
              'Content-Type': 'application/json'
            }
          }).then(res => {
            //debugger
            console.log(res.data);
            this.setState({
              showRM : false,
              showWorker : false,
              showConfigure : true,
              showCalculateCommission:false,
              w2mdata : res.data
            })
          }).catch(err => {
            console.log(err);
          })
        }

        handleSelect(index) {
          this.setState({
            resourceManagerId: this.state.data[index].resourceManagerId
          })
        }

  render () {

    return (
      <div className = "row">
      <Container>
      <Card.Body>
      <Col>
        <Button variant="primary" onClick= {this.handleResourceManager}>Resource Manager</Button>
        <Button variant="primary" onClick= {this.handleWorker}>Worker</Button>
        <Button variant="primary" onClick= {this.handleConfigure}>Configure Commission</Button>
        <Button variant="primary" onClick= {this.handleCalculateCommission}>Calculate Commission</Button>
        </Col>
      </Card.Body>
        <Modal show={this.state.showRM} onHide={this.handleClose}>
            <Modal.Header>
              <Modal.Title>Resource Managers</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <ResourceManager rows={this.state.data}/>
            </Modal.Body>
            <Modal.Footer>
              <Button variant="primary" onClick={this.handleClose}>
                Close
              </Button>
            </Modal.Footer>
        </Modal>

        <Modal show={this.state.showCalculateCommission} onHide={this.handleClose}>
            <Modal.Header>
              <Modal.Title>Calculate Commission</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <DropdownButton id="dropdown-basic" title="Dropdown button">
                  {this.state.data.map((row, index)  =>
                    <tr>
                      <Dropdown.Item onSelect={() => { this.handleSelect(index) }}>{this.state.data[index].name}</Dropdown.Item>
                    </tr>
                  )}
              </DropdownButton>
              <form>
                <label for="startDate">Choose Start Date</label>
                <input type="date" id="startDate" onChange={this.handleOnChange} value={this.state.startDate} />
                <label For="endDate">Choose End Date</label>
                <input type="date" id="endDate" onChange={this.handleOnChange} value={this.state.endDate} />
              </form>
            </Modal.Body>
            <Modal.Footer>
            <Button variant="primary" onClick={this.handleCalculate}>
                Calculate Commission
              </Button>
              <Button variant="primary" onClick={this.handleClose}>
                Close
              </Button>
            </Modal.Footer>
        </Modal>

        <Modal show={this.state.showWorker} onHide={this.handleClose}>
          <Modal.Header>
            <Modal.Title>Workers</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Worker rows={this.state.data}/>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="primary" onClick={this.handleClose}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>

        <Modal show={this.state.showConfigure} onHide={this.handleClose}>
          <Modal.Header>
            <Modal.Title>Projects</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Configure rows={this.state.w2mdata}/>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="primary" onClick={this.handleClose}>
              Close
            </Button>
          </Modal.Footer>
      </Modal>
      </Container>
    </div>

    )
  }

}

export default Tiles;

