import React, { Component } from 'react';
import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'
import Container from 'react-bootstrap/Container'
import Modal from 'react-bootstrap/Modal'
//import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Axios from 'axios';
import ResourceManager from './ResourceManager'
import Worker from './Worker'
//resource manager, resource, resource customer rate, resource salary, resource hour. commision rate
class Tiles extends Component {
  constructor(props, context) {
    super(props);
    this.state = {
      showRM: false,
      showWorker:false,
      data : []
    }
    this.handleResourceManager = this.handleResourceManager.bind(this);
    this.handleClose = this.handleClose.bind(this);
    this.handleWorker= this.handleWorker.bind(this);

  }

  handleClose(event) {
       this.setState({
         showRM: false,
         showWorker:false
       })
  }

  handleResourceManager(event) {
    event.preventDefault();
    // debugger
      Axios({
        url: 'http://localhost:9091/resource_manager/all', 
        method: "get",
        headers: {
          'Content-Type': 'application/json'
        }

      }).then(res => {
        // debugger
        console.log(res.data);
        this.setState({
          showRM : true,
          data : res.data
        })
      }).catch(err => {
        console.log(err);
      })
    }

    handleWorker(event) {
      event.preventDefault();
      // debugger
        Axios({
          url: 'http://localhost:9091/worker/all', 
          method: "get",
          headers: {
            'Content-Type': 'application/json'
          }

        }).then(res => {
          // debugger
          console.log(res.data);
          this.setState({
            showWorker : true,
            data : res.data
          })
        }).catch(err => {
          console.log(err);
        })
      }


  render () {

    return (
      <div className = "row">
      <Container>
      <Card.Body>
      <Col>
        <Button variant="primary" onClick={this.handleResourceManager}>Resource Manager</Button>
        <Button variant="primary" onClick= {this.handleWorker}>Worker</Button>
        <Button variant="primary">WorkerToManager</Button>
        <Button variant="primary">Bill Rate</Button>
        <Button variant="primary">Pay Rate</Button>
        <Button variant="primary">Commission Rate</Button>
        <Button variant="primary">Weekly Hours</Button>
        </Col>
      </Card.Body>
      <Modal show={this.state.showRM} onHide={this.handleClose}>
          <Modal.Header>
            <Modal.Title>Resource Managers</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <ResourceManager rows={this.state.data}/>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="primary" onClick={this.handleClose}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>

        <Modal show={this.state.showWorker} onHide={this.handleClose}>
          <Modal.Header>
            <Modal.Title>Workers</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Worker rows={this.state.data}/>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="primary" onClick={this.handleClose}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      </Container>
    </div>

    )
  }

}

export default Tiles;

