import React, { Component } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Axios from 'axios';

class ResourceManager extends Component {
  constructor(props) {
    super(props);
    this.state = {
      columns: [{ "header": "ID", "name": "resourceManagerId" },
      { "header": "NAME", "name": "name" },
      { "header": "Start Date", "name": "startDate" },
      { "header": "End Date", "name": "endDate" }],
      showAdd: false,
      showUpdate: false,
      showDelete: false,
      showRM: true,
      id: null,
      name: null,
      startDate: null,
      endDate: null
    }
    this.handleAdd = this.handleAdd.bind(this);
    this.handleUpdate = this.handleUpdate.bind(this);
    this.handleDelete = this.handleDelete.bind(this);
    this.handleClose = this.handleClose.bind(this);
    this.handleSubmitAdd = this.handleSubmitAdd.bind(this);
    this.handleSubmitUpdate = this.handleSubmitUpdate.bind(this);
  }

  handleSubmitAdd(event) {
    event.preventDefault();
    console.log("Form Add submitted");
    if (this.state.name) {
      let userData = {
        name: this.state.name,
        startDate: this.state.startDate,
        endDate: this.state.endDate
      }
      debugger
      Axios({
        url: 'http://localhost:9091/resource_manager/',
        method: "post",
        data: userData,
        headers: {
          'Content-Type': 'application/json'
        }

      }).then(res => {
        debugger
        console.log(this.props);
      }).catch(err => {
        console.log(err);
      })
    }

    this.setState({
      id: null,
      name: null,
      startDate: null,
      endDate: null,
      showAdd: false,
      showDelete: false,
      showUpdate: false,
      showRM: false
    })
  }

  handleSubmitUpdate(event) {
    event.preventDefault();
    console.log("Form Update submitted");
    if (this.state.name) {
      let userData = {
        resourceManagerId: this.state.id,
        name: this.state.name,
        startDate: this.state.startDate,
        endDate: this.state.endDate
      }
      debugger
      Axios({
        url: 'http://localhost:9091/resource_manager/' + this.state.id,
        method: "put",
        data: userData,
        headers: {
          'Content-Type': 'application/json'
        }

      }).then(res => {
        debugger
        console.log(this.props);
      }).catch(err => {
        console.log(err);
      })
    }
    this.setState({
      id: null,
      name: null,
      startDate: null,
      endDate: null,
      showAdd: false,
      showDelete: false,
      showUpdate: false,
      showRM: false
    })
  }

  handleSubmitDelete(event) {
    // event.preventDefault();
    console.log("Form Delete submitted");
    debugger
    if (this.state.id) {
      Axios({
        url: 'http://localhost:9091/resource_manager/' + this.state.id,
        method: "delete",
        data: "",
        headers: {
          'Content-Type': 'application/json'
        }

      }).then(res => {
        event.preventDefault();
        debugger
        console.log(this.props);
      }).catch(err => {
        console.log(err);
      })
    }
    this.setState({
      id: null,
      name: null,
      startDate: null,
      endDate: null,
      showAdd: false,
      showDelete: false,
      showUpdate: false,
      showRM: false
    })
  }

  handleAdd(event) {
    this.setState({
      showAdd: true,
      showDelete: false,
      showUpdate: false,
      showRM: false
    })
  }

  handleUpdate = (id) => {
    debugger;
    var startD = new Date(this.props.rows[id - 1].startDate);
    var endD = new Date(this.props.rows[id - 1].endDate);
    //start date 
    var month1 = startD.getMonth();

    if (month1 < 10) {
      month1 = ("0" + (month1 + 1));
    }
    else {
      month1 = ("" + (month1 + 1));
    }

    var day1 = startD.getDate();
    if (day1 < 10) {
      day1 = ("0" + day1);
    }
    else {
      day1 = ("" + day1);
    }

    var year1 = "" + startD.getFullYear();
    var fullYear1 = year1 + '-' + month1 + '-' + day1;

    //end date
    var month2 = endD.getMonth();

    if (month2 < 10) {
      month2 = ("0" + (month2 + 1));
    }
    else {
      month2 = ("" + (month2 + 1));
    }
    var day2 = endD.getDate();
    if (day2 < 10) {
      day2 = ("0" + day2);
    }
    else {
      day2 = ("" + day2);
    }
    var year2 = "" + endD.getFullYear();

    var fullYear2 = year2 + '-' + month2 + '-' + day2;

    this.setState({
      id: id,
      name: this.props.rows[id - 1].name,
      fullYearStart: fullYear1,
      fullYearEnd: fullYear2,
      showUpdate: true,
      showAdd: false,
      showDelete: false,
      showRM: false
    })
  }

  handleDelete(event) {
    this.setState({
      showDelete: true,
      showAdd: false,
      showUpdate: false,
      showRM: false
    })
  }

  handleClose(event) {
    this.setState({
      showUpdate: false,
      showDelete: false,
      showAdd: false,
      showRM: false

    })
  }

  handleOnChange = (event) => {
    debugger
    console.log("line 88", event.target.value);
    let obj = {}

    obj[event.target.id] = event.target.value;

    this.setState({

        ...this.state.id,

        ...this.state.name,

        ...this.state.startDate,

        ...this.state.endDate,

        ...obj


    })

    console.log("onchange =? obj => ", obj)
    console.log('this.state => ', this.state)

  }



  render() {
    console.log("props--------", this.props);
    console.log("line 314", this.state);
    return (
      <div>
        <table>
          <thead>
            <tr>
              {this.state.columns.map(col =>
                <th key={col.name}>{col.header}</th>
              )}
            </tr>
          </thead>
          <tbody>
            {this.props.rows.map(row =>
              <tr key={row.id} >
                {this.state.columns.map(col =>
                  <td key={col.name}>{row[col.name]}</td>
                )}
                <Button variant="primary" onClick={() => { this.handleUpdate(row.resourceManagerId) }} > update {row.resourceManagerId} </Button>
              </tr>
            )}
          </tbody>
        </table>
        <Button variant="inherit" onClick={this.handleAdd}>
          Add
        </Button>

        <Modal show={this.state.showUpdate} onHide={this.handleClose}>
          <Modal.Header>
            <Modal.Title>Update Resource Manager</Modal.Title>
          </Modal.Header>
          <form onSubmit={this.handleSubmitUpdate}>
            <Modal.Body>
              <br />
              <label For="id">ID</label>
              <br/>
              <label For="id" >{this.state.id}</label>
              <br/>
              <label For="name">Name</label>
              <input id="name" name="name" type="text" onChange={this.handleOnChange} value={this.state.name} />
              <label for="startDate">Start Date</label>
              <input type="date" id="startDate" class="form-control" onChange={this.onChange} value={this.state.fullYearStart} />
              <label For="endDate">End Date</label>
              <input type="date" id="endDate" class="form-control" onChange={this.onChange} value={this.state.fullYearEnd} />
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={this.handleClose}>
                Close
                </Button>
              <Button type="submit" variant="primary" >
                Submit
                </Button>
            </Modal.Footer>
          </form>
        </Modal>


        <Modal show={this.state.showDelete} onHide={this.handleClose}>
          <Modal.Header>
            <Modal.Title>Delete New Resource Manager</Modal.Title>
          </Modal.Header>
          <form onSubmit={this.handleSubmitDelete}>
            <Modal.Body>
              <label For="ID">ID</label>
              <input id="ID" type="text" onChange={this.onChange} value={this.state.id} />
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={this.handleClose}>
                Close
                </Button>
              <Button type="submit" variant="primary" >
                Submit
                </Button>
            </Modal.Footer>
          </form>
        </Modal>

        <Modal show={this.state.showAdd} onHide={this.handleClose}>
          <Modal.Header>
            <Modal.Title>Add New Resource Manager</Modal.Title>
          </Modal.Header>
          <form onSubmit={this.handleSubmitAdd}>
            <Modal.Body>
              <label For="name">Name</label>
              <input id="name" name="name" type="text" onChange={this.onChange} value={this.state.name} />
              <label for="startDate">Start Date</label>
              <input type="date" id="startDate" class="form-control" onChange={this.onChange} value={this.state.startDate} />
              <label For="endDate">End Date</label>
              <input type="date" id="endDate" class="form-control" onChange={this.onChange} value={this.state.endDate} />
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={this.handleClose}>
                Close
                </Button>
              <Button type="submit" variant="primary" >
                Submit
                </Button>
            </Modal.Footer>
          </form>
        </Modal>

      </div>
    );
  }
}

export default ResourceManager;
