    this.setState({//hides the modal
      id: null,
      name: null,
      showAdd: false,
      showDelete: false,
      showUpdate: false,
      showWorker: false
    })
  }

  handleSubmitUpdate(event) {
    event.preventDefault();
    console.log("Form Update submitted");
    if (this.state.name) {
      let userData = {
        workerId: this.state.id,
        name: this.state.name,
      }
      debugger
      Axios({ //when updating information
        url: 'http://localhost:9091/worker/' + this.state.id, //this.state.id= value /{id}
        method: "put",
        data: userData,
        headers: {
          'Content-Type': 'application/json'
        }

      }).then(res => {
        debugger
        console.log(this.props);
      }).catch(err => {
        console.log(err);
      })
    }
    this.setState({
      id: null,
      name: null,
      showAdd: false,
      showDelete: false,
      showUpdate: false,
      showWorker: false  
    })
  }

  handleSubmitDelete(event) {//deleteing data
    // event.preventDefault();
    console.log("Form Delete submitted");
    debugger
    if (this.state.id) {
      Axios({
        url: 'http://localhost:9091/worker/' + this.state.id,
        method: "delete",
        data: "",
        headers: {
          'Content-Type': 'application/json'
        }

      }).then(res => {
        event.preventDefault();
        debugger
        console.log(this.props);
      }).catch(err => {
        console.log(err);
      })
    }
    this.setState({
      id: null,
      name: null,
      showAdd: false,
      showDelete: false,
      showUpdate: false,
      showWorker: false
    })
  }

  handleAdd(event) {//help with this line of code
    this.setState({
      showAdd: true,
      showDelete: false,
      showUpdate: false,
      showWorker: false
    })
  }

  handleUpdate = (id) => {
    debugger;


    this.setState({
      id: id,
      name: this.props.rows[id - 1].name,
      showAdd: false,
      showUpdate:true,
      showDelete: false,
      showWorker: false
    })
  }

  handleDelete(event) {
    this.setState({
      showDelete: true,
      showAdd: false,
      showUpdate: false,
      showWorker: false
    })
  }

  handleClose(event) {
    this.setState({
      showUpdate: false,
      showDelete: false,
      showAdd: false,
      showWorker: false

    })
  }

  handleOnChange = (event) => {
    debugger
    console.log("line 88", event.target.value);
    let obj = {}

    obj[event.target.id] = event.target.value;

    this.setState({

        ...this.state.id,

        ...this.state.name,

        ...obj


    })

    console.log("onchange =? obj => ", obj)
    console.log('this.state => ', this.state)

  }



  render() {
    console.log("props--------", this.props);
    console.log("line 314", this.state);
    return (
      <div>
        <table>
          <thead>
            <tr>
              {this.state.columns.map(col =>
                <th key={col.name}>{col.header}</th>
              )}
            </tr>
          </thead>
          <tbody>
            {this.props.rows.map(row =>
              <tr key={row.id} >
                {this.state.columns.map(col =>
                  <td key={col.name}>{row[col.name]}</td>
                )}
                <Button variant="primary" onClick={() => { this.handleUpdate(row.workerId) }} > update  </Button>
              </tr>
            )}
          </tbody>
        </table>
        <Button variant="inherit" onClick={this.handleAdd}>
          Add
        </Button>

        <Modal show={this.state.showUpdate} onHide={this.handleClose}>
          <Modal.Header>
            <Modal.Title>Update Worker</Modal.Title>
          </Modal.Header>
          <form onSubmit={this.handleSubmitUpdate}>
            <Modal.Body>
              <br />
              <label For="id">ID</label>
              <br/>
              <label For="id" >{this.state.id}</label>
              <br/>
              <label For="name">Name</label>
              <input id="name" name="name" type="text" onChange={this.handleOnChange} value={this.state.name} />
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={this.handleClose}>
                Close
                </Button>
              <Button type="submit" variant="primary" >
                Submit
                </Button>
            </Modal.Footer>
          </form>
        </Modal>


        <Modal show={this.state.showDelete} onHide={this.handleClose}>
          <Modal.Header>
            <Modal.Title>Delete New Worker</Modal.Title>
          </Modal.Header>
          <form onSubmit={this.handleSubmitDelete}>
            <Modal.Body>
              <label For="ID">ID</label>
              <input id="ID" type="text" onChange={this.handleOnChange} value={this.state.id} />
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={this.handleClose}>
                Close
                </Button>
              <Button type="submit" variant="primary" >
                Submit
                </Button>
            </Modal.Footer>
          </form>
        </Modal>

        <Modal show={this.state.showAdd} onHide={this.handleClose}>
          <Modal.Header>
            <Modal.Title>Add Worker</Modal.Title>
          </Modal.Header>
          <form onSubmit={this.handleSubmitAdd}>
            <Modal.Body>
              <label For="name">Name</label>
              <input id="name" name="name" type="text" onChange={this.handleOnChange} value={this.state.name} />
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={this.handleClose}>
                Close
                </Button>
              <Button type="submit" variant="primary" >
                Submit
                </Button>
            </Modal.Footer>
          </form>
        </Modal>

      </div>
    );
  }
}

export default Worker;
