import React, { Component } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Axios from 'axios';
import { isPropertySignature } from 'typescript';

class BillRate extends Component {
  constructor(props) {
    super(props);
    this.state = {
      columns: [{ "header": "Rate", "name": "rate" },
      { "header": "Start Date", "name": "startDate" },
      { "header": "End Date", "name": "endDate" }],
      showAdd: false,
      showUpdate: false,
      showDelete: false,
      showRM: true,
      startDate: null,
      endDate: null,
      uniqueId: this.props.id
    }
    this.handleOnChange = this.handleOnChange.bind(this);
    this.handleAdd    = this.handleAdd.bind(this);
    this.handleUpdate = this.handleUpdate.bind(this);
    this.handleDelete = this.handleDelete.bind(this);
    this.handleClose = this.handleClose.bind(this);
    this.handleSubmitAdd = this.handleSubmitAdd.bind(this);
    this.handleSubmitUpdate = this.handleSubmitUpdate.bind(this);
    this.handleSubmitDelete = this.handleSubmitDelete.bind(this);
  }

  handleSubmitAdd(event) {
    debugger
    event.preventDefault();
    console.log("Form Add submitted");
    //if (this.state.name) {
    debugger
    let userData = {
        uniqueId: this.props.uniqueId,
      rate: this.state.name,
      startDate: this.state.startDate,
      endDate: this.state.endDate
    }
    debugger
    Axios({
      url: 'http://localhost:9091/bill_rate/',
      method: "post",
      data: userData,
      headers: {
        'Content-Type': 'application/json'
      }

    }).then(res => {
      debugger
      console.log(this.props);
    }).catch(err => {
      console.log(err);
    })
    //}
    console.log(this.state.startDate);

    this.setState({
      id: null,
      name: null,
      startDate: null,
      endDate: null,
      showAdd: false,
      showDelete: false,
      showUpdate: false,
      showRM: false
    })
  }

  handleSubmitUpdate(event) {
    event.preventDefault();
    console.log("Form Update submitted");
    if (this.state.name) {
      let userData = {
        resourceManagerId: this.state.id,
        name: this.state.name,
        startDate: this.state.startDate,
        endDate: this.state.endDate
      }
      Axios({
        url: 'http://localhost:9091/resource_manager/' + this.state.id,
        method: "put",
        data: userData,
        headers: {
          'Content-Type': 'application/json'
        }

      }).then(res => {
        console.log(this.props);
      }).catch(err => {
        console.log(err);
      })
    }
    this.setState({
      id: null,
      name: null,
      startDate: null,
      endDate: null,
      showAdd: false,
      showDelete: false,
      showUpdate: false,
      showRM: false
    })
  }

  handleSubmitDelete(event) {
    event.preventDefault();
    console.log("Form Delete submitted");
    debugger
    if (this.state.id) {
      Axios({
        url: 'http://localhost:9091/resource_manager/' + this.state.id,
        method: "delete",
        data: "",
        headers: {
          'Content-Type': 'application/json'
        }

      }).then(res => {
        event.preventDefault();
        debugger
        console.log(this.props);
      }).catch(err => {
        console.log(err);
      })
    }
    this.setState({
      id: null,
      name: null,
      startDate: null,
      endDate: null,
      showAdd: false,
      showDelete: false,
      showUpdate: false,
      showRM: false
    })
  }

  handleAdd(event) {
    this.setState({
      showAdd: true,
      showDelete: false,
      showUpdate: false,
      showRM: false
    })
  }

  handleUpdate = (id) => {
    debugger;
    console.log(id);
    console.log(this.props.rows[id]);
    console.log(this.props.rows[id].name);
    console.log(this.props.rows[id].resourceManagerId);
    console.log(this.props.rows[id].startDate);
    console.log(this.props.rows[id].endDate);
    var resourceManagerId = this.props.rows[id].resourceManagerId;
    console.log(id);
    var startDate = this.props.rows[id].startDate;
    console.log(startDate);
    var endDate = this.props.rows[id].endDate;
    this.setState({
      id: resourceManagerId,
      name: this.props.rows[id].name,
      startDate: startDate,
      //startDate: date,
      endDate: endDate,
      showUpdate: true,
      showAdd: false,
      showDelete: false,
      showRM: false
    })
  }
  handleDelete = (id) => {
    debugger
    this.setState({
      id: id,
      showDelete: true,
      showAdd: false,
      showUpdate: false,
    })
  }

  handleClose(event) {
    this.setState({
      showUpdate: false,
      showDelete: false,
      showAdd: false,
      showRM: false

    })
  }

  handleOnChange = (event) => {
    debugger
    console.log("line 88", event.target.value);
    let obj = {}

    obj[event.target.id] = event.target.value;

    this.setState({

      ...this.state.id,

      ...this.state.name,

      ...this.state.startDate,

      ...this.state.endDate,

      ...obj


    })

    console.log("onchange =? obj => ", obj)
    console.log('this.state => ', this.state)

  }



  render() {
    console.log("props--------", this.props);
    console.log("line 314", this.state);
    return (
      <div>
        <table>
          <thead>
            <tr>
              {this.state.columns.map(col =>
                <th key={col.name}>{col.header}</th>
              )}
            </tr>
          </thead>
          <tbody>
            {this.props.rows.map((row, index) =>
              <tr key={row.id} >
                {this.state.columns.map(col =>
                  <td key={col.name}>{row[col.name]}</td>
                )}
                <Button variant="primary" onClick={() => { this.handleUpdate(index) }} > update </Button>
                <Button variant="primary" onClick={() => { this.handleDelete(row.resourceManagerId) }} > delete </Button>
              </tr>
            )}
          </tbody>
        </table>
        <Button variant="inherit" onClick={this.handleAdd}> Add</Button>

        <Modal show={this.state.showUpdate} onHide={this.handleClose}>
          <Modal.Header>
            <Modal.Title>Update Resource Manager</Modal.Title>
          </Modal.Header>
          <form onSubmit={this.handleSubmitUpdate}>
            <Modal.Body>
              <br />
              <label For="id">ID</label>
              <br />
              <label For="id" >{this.state.id}</label>
              <br />
              <label For="name">Name</label>
              <input id="name" name="name" type="text" onChange={this.handleOnChange} value={this.state.name} />
              <label for="startDate">Start Date</label>
              <input type="date" id="startDate" onChange={this.handleOnChange} value={this.state.startDate} />
              <label For="endDate">End Date</label>
              <input type="date" id="endDate" onChange={this.handleOnChange} value={this.state.endDate} />
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={this.handleClose}>
                Close
                </Button>
              <Button type="submit" variant="primary" >
                Submit
                </Button>
            </Modal.Footer>
          </form>
        </Modal>


        <Modal show={this.state.showDelete} onHide={this.handleClose}>
          <Modal.Header>
            <Modal.Title>Are you sure you want to Delete?</Modal.Title>
          </Modal.Header>
          <form onSubmit={this.handleSubmitDelete}>
            <Modal.Body>
              <label For="ID">Resource Manager ID:{this.state.id} </label>
              <input id="ID" type="text" onChange={this.handleOnChange} value={this.state.id} />
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={this.handleClose}>
                No
                </Button>
              <Button type="submit" variant="primary">
                Yes
                </Button>
            </Modal.Footer>
          </form>
        </Modal>

       <Modal show={this.state.showAdd} onHide={this.handleClose}>
          <Modal.Header>
            <Modal.Title>Add New Resource Manager</Modal.Title>
          </Modal.Header>
          <form onSubmit={this.handleSubmitAdd}>
            <Modal.Body>
              <label For="name">Name</label>
              <input id="name" name="name" type="text" onChange={this.handleOnChange} value={this.state.name} />
              <label for="startDate">Start Date</label>
              <input type="date" id="startDate" class="form-control" onChange={this.handleOnChange} value={this.state.startDate} />
              <label For="endDate">End Date</label>
              <input type="date" id="endDate" class="form-control" onChange={this.handleOnChange} value={this.state.endDate} />
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={this.handleClose}>
                Close
                </Button>
              <Button type="submit" variant="primary" >
                Submit
                </Button>
            </Modal.Footer>
          </form> 
        </Modal>

      </div>
    );
  }
}

export default BillRate;
