import React, { Component } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
//import Accordion from 'react-bootstrap/Accordion';
import Axios from 'axios';
import WorkerToManager from './WorkerToManager';

class Worker extends Component {
  constructor(props) {
    super(props);
    this.state = {
      columns: [{ "header": "ID", "name": "workerId" },
      { "header": "NAME", "name": "name" }],
      showAdd: false,
      showUpdate: false,
      showDelete: false,
      showWorkerToManager: false,
      showWorker: true,
      showRM: false,
      id: null,
      name: null,
      data: [],
      workerName: null,
      workerId: null,
      names: [],
      nameMap: {}
    }
    this.handleWorkerToManager= this.handleWorkerToManager.bind(this);
    this.handleAdd = this.handleAdd.bind(this);
    this.handleUpdate = this.handleUpdate.bind(this);
    this.handleDelete = this.handleDelete.bind(this);
    this.handleClose = this.handleClose.bind(this);
    this.handleBack = this.handleBack.bind(this);
    this.handleSubmitAdd = this.handleSubmitAdd.bind(this);
    this.handleSubmitUpdate = this.handleSubmitUpdate.bind(this);
    this.handleSubmitDelete = this.handleSubmitDelete.bind(this);
  }

  handleSubmitAdd(event) {
    event.preventDefault();
    console.log("Form Add submitted"); 
    if (this.state.name) {
      let userData = {
        name: this.state.name,
      }
      debugger
      Axios({
        url: 'http://localhost:9091/worker/', 
        method: "post",
        data: userData,
        headers: {
          'Content-Type': 'application/json'
        }

      }).then(res => {//help on understanding these few lines
        debugger
        console.log(this.props);
        this.refresh();
        this.setState({//hides the modal
          id: null,
          name: null,
          showAdd: false,
          showDelete: false,
          showUpdate: false,
          showWorkerToManager: false,
          showWorker: false
        })
      }).catch(err => {
        console.log(err);
      })
    }
  }

  handleSubmitDelete(event) {//deleteing data
    // event.preventDefault();
    console.log("Form Delete submitted");
    debugger
    if (this.state.id) {
      Axios({
        url: 'http://localhost:9091/worker/' + this.state.id,
        method: "delete",
        data: "",
        headers: {
          'Content-Type': 'application/json'
        }

      }).then(res => {
        event.preventDefault();
        debugger
        console.log(this.props);
        this.refresh();
        this.setState({
          id: null,
          name: null,
          showAdd: false,
          showDelete: false,
          showUpdate: false,
          showWorkerToManager: false,
          showWorker: true
        })
      }).catch(err => {
        console.log(err);
      })
    }
  }

  handleSubmitUpdate(event) {
    event.preventDefault();
    console.log("Form Update submitted");
    if (this.state.name) {
      let userData = {
        workerId: this.state.id,
        name: this.state.name,
      }
      debugger
      Axios({ //when updating information
        url: 'http://localhost:9091/worker/' + this.state.id, //this.state.id= value /{id}
        method: "put",
        data: userData,
        headers: {
          'Content-Type': 'application/json'
        }

      }).then(res => {
        debugger
        console.log(this.props);
        this.refresh();
        this.setState({
          id: null,
          name: null,
          showAdd: false,
          showDelete: false,
          showUpdate: false,
          showWorkerToManager: false,
          showWorker: false  
        })
      }).catch(err => {
        console.log(err);
      })
    }
  }

  handleWorkerToManager(id) {
    // event.preventDefault();
    debugger;
      Axios({
        url: 'http://localhost:9091/worker_to_manager/all/' + this.props.rows[id].workerId, 
        method: "get",
        headers: {
          'Content-Type': 'application/json'
        }
      }).then(res => {
        debugger;
        console.log(res.data);
        this.setState({
          workerId : this.props.rows[id].workerId,
          showWorker : false,
          showWorkerToManager : true,
          showRM: false,
          w2mdata : res.data,
          workerName: this.props.rows[id].name
        })
      }).catch(err => {
        console.log(err);
      })

      Axios({
        url: 'http://localhost:9091/resource_manager/all', 
        method: "get",
        headers: {
          'Content-Type': 'application/json'
        }
      }).then(res => {
        // debugger
        console.log(res.data);
        var names = [];
        var nameMap = {};
        {res.data.map((row, index)  =>
          names.push(res.data[index].name)
        )}
        {res.data.map((row, index)  =>
          nameMap [res.data[index].name]= res.data[index].resourceManagerId
        )}
        this.setState({
          rmdata : res.data,
          names: names,
          nameMap: nameMap
        })
      }).catch(err => {
        console.log(err);
      })
    }


  handleAdd(event) {//help with this line of code
    this.setState({
      showAdd: true,
      showDelete: false,
      showUpdate: false,
      showWorkerToManager: false,
      showWorker: false
    })
  }

  handleUpdate = (id) => {
    debugger;
    this.setState({
      id: this.props.rows[id].workerId,
      name: this.props.rows[id].name,
      showUpdate: true,
      showAdd: false,
      showDelete: false,
      showWorkerToManager: false
    })
  }

  handleBack(event) {
    debugger;
    this.setState({
      showUpdate: false,
      showAdd: false,
      showDelete: false,
      showWorkerToManager: false,
      showWorker: true
    })
  }

  handleDelete(id) {
    this.setState({
      id: this.props.rows[id].workerId,
      showDelete: true,
      showAdd: false,
      showUpdate: false,
      showWorkerToManager: false
    })
  }

  handleClose(event) {
    this.setState({
      showUpdate: false,
      showDelete: false,
      showAdd: false,
      showWorker: false,
      showWorkerToManager: false      
    })
  }

  handleOnChange = (event) => {
    debugger
    let obj = {}

    obj[event.target.id] = event.target.value;

    this.setState({

        ...this.state.id,

        ...this.state.name,

        ...obj


    })

    console.log("onchange =? obj => ", obj)
    console.log('this.state => ', this.state)

  }

  refresh() {
    Axios({
      url: 'http://localhost:9091/worker/all', 
      method: "get",
      headers: {
        'Content-Type': 'application/json'
      }

    }).then(res => {
      // debugger
      console.log(res.data);
      this.setState({
        showUpdate: false,
        showDelete: false,
        showAdd: false,
        showWorker: false,
