import React, { Component } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
//import Accordion from 'react-bootstrap/Accordion';
import Axios from 'axios';
import WorkerToManager from './WorkerToManager';

class Worker extends Component {
  constructor(props) {
    super(props);
    this.state = {
      columns: [{ "header": "ID", "name": "workerId" },
      { "header": "NAME", "name": "name" }],
      showAdd: false,
      showUpdate: false,
      showDelete: false,
      showWorkerToManager: false,
      showWorker: true,
      showRM: false,
      id: null,
      name: null
    }
    this.handleWorkerToManager= this.handleWorkerToManager.bind(this);
    this.handleAdd = this.handleAdd.bind(this);
    this.handleUpdate = this.handleUpdate.bind(this);
    this.handleDelete = this.handleDelete.bind(this);
    this.handleClose = this.handleClose.bind(this);
    this.handleBack = this.handleBack.bind(this);
    this.handleSubmitAdd = this.handleSubmitAdd.bind(this);
    this.handleSubmitUpdate = this.handleSubmitUpdate.bind(this);
    this.handleSubmitDelete = this.handleSubmitDelete.bind(this);
  }

  handleSubmitAdd(event) {
    event.preventDefault();
    console.log("Form Add submitted"); 
    if (this.state.name) {
      let userData = {
        name: this.state.name,
      }
      debugger
      Axios({
        url: 'http://localhost:9091/worker/', 
        method: "post",
        data: userData,
        headers: {
          'Content-Type': 'application/json'
        }

      }).then(res => {//help on understanding these few lines
        debugger
        console.log(this.props);
      }).catch(err => {
        console.log(err);
      })
    }

    this.setState({//hides the modal
      id: null,
      name: null,
      showAdd: false,
      showDelete: false,
      showUpdate: false,
      showWorkerToManager: false,
      showWorker: false
    })
  }

  handleSubmitDelete(event) {//deleteing data
    // event.preventDefault();
    console.log("Form Delete submitted");
    debugger
    if (this.state.id) {
      Axios({
        url: 'http://localhost:9091/worker/' + this.state.id,
        method: "delete",
        data: "",
        headers: {
          'Content-Type': 'application/json'
        }

      }).then(res => {
        event.preventDefault();
        debugger
        console.log(this.props);
      }).catch(err => {
        console.log(err);
      })
    }
    this.setState({
      id: null,
      name: null,
      showAdd: false,
      showDelete: false,
      showUpdate: false,
      showWorkerToManager: false,
      showWorker: true
    })
  }

  handleSubmitUpdate(event) {
    event.preventDefault();
    console.log("Form Update submitted");
    if (this.state.name) {
      let userData = {
        workerId: this.state.id,
        name: this.state.name,
      }
      debugger
      Axios({ //when updating information
        url: 'http://localhost:9091/worker/' + this.state.id, //this.state.id= value /{id}
        method: "put",
        data: userData,
        headers: {
          'Content-Type': 'application/json'
        }

      }).then(res => {
        debugger
        console.log(this.props);
      }).catch(err => {
        console.log(err);
      })
    }
    this.setState({
      id: null,
      name: null,
      showAdd: false,
      showDelete: false,
      showUpdate: false,
      showWorkerToManager: false,
      showWorker: false  
    })
  }

  handleWorkerToManager(index) {
    // event.preventDefault();
    debugger;
      Axios({
        url: 'http://localhost:9091/worker_to_manager/all/' + index, 
        method: "get",
        headers: {
          'Content-Type': 'application/json'
        }
      }).then(res => {
        debugger;
        console.log(res.data);
        this.setState({
          showWorker : false,
          showWorkerToManager : true,
          showRM: false,
          data : res.data
        })
      }).catch(err => {
        console.log(err);
      })
    }


  handleAdd(event) {//help with this line of code
    this.setState({
      showAdd: true,
      showDelete: false,
      showUpdate: false,
      showWorkerToManager: false,
      showWorker: false
    })
  }

  handleUpdate = (id) => {
    debugger;
    this.setState({
      id: this.props.rows[id].workerId,
      name: this.props.rows[id].name,
      showUpdate: true,
      showAdd: false,
      showDelete: false,
      showWorkerToManager: false
    })
  }

  handleBack(event) {
    debugger;
    this.setState({
      showUpdate: false,
      showAdd: false,
      showDelete: false,
      showWorkerToManager: false,
      showWorker: true
    })
  }

  handleDelete(id) {
    this.setState({
      id: this.props.rows[id].workerId,
      showDelete: true,
      showAdd: false,
      showUpdate: false,
      showWorkerToManager: false
    })
  }

  handleClose(event) {
    this.setState({
      showUpdate: false,
      showDelete: false,
      showAdd: false,
      showWorker: false,
      showWorkerToManager: false      
    })
  }

  handleOnChange = (event) => {
    debugger
    let obj = {}

    obj[event.target.id] = event.target.value;

    this.setState({

        ...this.state.id,

        ...this.state.name,

        ...obj


    })

    console.log("onchange =? obj => ", obj)
    console.log('this.state => ', this.state)

  }

  render() {
    console.log("props--------", this.props);
    console.log("line 314", this.state);
    return (
      <div>
        <table>
          <thead>
            <tr>
              {this.state.columns.map(col =>
                <th key={col.name}>{col.header}</th>
              )}
            </tr>
          </thead>
              <tbody>
                {this.props.rows.map((row, index)  =>
                  <tr key={row.id} data-toggle="collapse" data-target="#accordion" class="clickable">
                    {this.state.columns.map(col =>
                      <td key={col.name}>{row[col.name]}</td>
                    )}
                    <Button variant="primary" onClick={() => { this.handleUpdate(index) }} > update {index}</Button>
                    <Button variant="primary" onClick={() => { this.handleDelete(index) }} > delete {index}</Button>
                    <Button variant="primary" onClick={() => { this.handleWorkerToManager(this.props.rows[index].workerId) }}> worker's records {this.props.rows[index].workerId}</Button>
                  </tr>
                )}
              </tbody>
        </table>
        <Button variant="inherit" onClick={this.handleAdd}>
          Add
        </Button>

        <Modal show={this.state.showDelete} onHide={this.handleClose}>
          <Modal.Header>
            <Modal.Title>Delete New Worker</Modal.Title>
          </Modal.Header>
          <form onSubmit={this.handleSubmitDelete}>
            <Modal.Body>
              <label For="ID">ID</label>
              <input id="ID" type="text" onChange={this.handleOnChange} value={this.state.id} />
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={this.handleClose}>
                Close
                </Button>
              <Button type="submit" variant="primary" >
                Submit
                </Button>
            </Modal.Footer>
          </form>
        </Modal>

        <Modal show={this.state.showAdd} onHide={this.handleClose}>
          <Modal.Header>
            <Modal.Title>Add Worker</Modal.Title>
          </Modal.Header>
          <form onSubmit={this.handleSubmitAdd}>
            <Modal.Body>
              <label For="name">Name</label>
              <input id="name" name="name" type="text" onChange={this.handleOnChange} value={this.state.name} />
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={this.handleClose}>
                Close
                </Button>
              <Button type="submit" variant="primary" >
                Submit
                </Button>
            </Modal.Footer>
          </form>
        </Modal>

        <Modal show={this.state.showUpdate} onHide={this.handleClose}>
          <Modal.Header>
            <Modal.Title>Update Worker</Modal.Title>
          </Modal.Header>
          <form onSubmit={this.handleSubmitUpdate}>
            <Modal.Body>
              <br />
              <label For="id">ID</label>
              <br/>
              <label For="id" >{this.state.id}</label>
              <br/>
              <label For="name">Name</label>
              <input id="name" name="name" type="text" onChange={this.handleOnChange} value={this.state.name} />
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={this.handleClose}>
                Close
                </Button>
              <Button type="submit" variant="primary" >
                Submit
                </Button>
            </Modal.Footer>
          </form>
        </Modal>

        <Modal show={this.state.showWorkerToManager} onHide={this.handleBack}>
          <Modal.Header>
            <Modal.Title>{this.state.name}'s Record</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <WorkerToManager rows={this.state.data}/>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="primary" onClick={this.handleBack}>
              Back
            </Button>
          </Modal.Footer>
        </Modal>

      </div>);
  }
}

export default Worker;
